import React, { Component } from 'react';
import './App.css';
import DogSpan from './DogSpan'
import DogInfo from './DogInfo'

let pupsUrl = 'http://localhost:3000/pups/'

class App extends Component {

  state = {
    dogs: [],
    displayDog: 0,
    goodDogs: []
  }

  componentDidMount(){
    fetch(pupsUrl)
    .then(res => res.json())
    .then(dogs => {
      this.setState({
        dogs: dogs,
        goodDogs: dogs.filter(dog => dog.isGoodDog === true)
      })
    })
  }

  renderDog = (dog) => {
    this.setState({
      displayDog: dog
    })
  }

  changeCharacter = (dog) => {
    this.setState({
      displayDog: {...dog, isGoodDog: !dog.isGoodDog},
      goodDogs: this.state.dogs.filter(dog => dog.isGoodDog === true)
    })
  }

  render() {
    return(
    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div id="dog-bar">
          {this.state.dogs.map(dog => <DogSpan dog={dog} key={dog.id} renderDog={this.renderDog}/>)}
      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div id="dog-info">
          {(this.state.displayDog === 0) ? null : <DogInfo dog={this.state.displayDog} changeCharacter={this.changeCharacter}/>}
        </div>
      </div>
    </div>
    )
  };
}

export default App;
